/* ------------
    Footnotes
   ------------
*/
span[id^='fn'], span[class^='fn'] {
    font-size: small !important;
    font-weight: normal !important;
    font-style: normal !important;
    border-top: 1px solid var(--txbk-ui-mono-color);
    padding-top: .8rem;
    display: block;
}

p > span[id^='fn']:not(:first-child),
p > span[class^='fn']:not(:first-child) {
    border-top: 0 !important;
}

[id^='fn'] i, [class^='fn'] i, p > strong > strong {
    background-color: var(--txbk-content-mono-bgcolor);
    font-style: normal !important;
    font-weight: bold !important;
    text-transform: capitalize;
}

/* FIX Forcing monospace code to adhere with footnotes rules!*/
div.markdown p > [id^='fn'] code,
div.markdown p > [class^='fn'] code {
    font-size: 1em !important;
}

/* ------------
    Drop cap
   ------------
*/
.drop {
    color: var(--drop-cap-color);
    float: left;
    font-size: 75px;
    line-height: 60px;
    padding-top: 4px;
    padding-right: 8px;
    padding-left: 3px;
    margin-top: 0.2rem;
}


